  const APP_ID = '84cc3ca8'
  const APP_KEY = '74cf5c57d29fcd88151fe84324c26e71'
  const recipeApi = `https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`

  useEffect(async () => {
    getRecipes()
  }, [])

  useEffect(() => {
    async function getRecipes() {
      // You can await here
      const response = await recipeApi.getData(someId)
      // ...
    }
    getRecipes()
  }, [])

  const getRecipes = async () => {
    const response = await fetch(recipeApi)
    const data = await response.json()
    console.log(data)
  }

  return (
    <div className='App'>
      <form className='search-form'>
        <input className='search-bar' type='text' />
        <button className='search-button' type='submit'>
          Search
        </button>
      </form>
    </div>
  )
}

export default App
